(function($){"use strict";$.fn.tabList=function(options){var defaults={tabContent:"",icon:true,callback:function(){},mouseEvent:"click",autoPlay:false,autoPlaySpeed:5e3};var o=$.extend(defaults,options);return this.each(function(){var tabs=$(this).children("li");var tabsCount=tabs.length;var tabLinks=tabs.children("a");var tabContent=o.tabContent;var currentTab=0;var selectedTab;var timer;var tabify=function(el){selectTab(el);showContent(String(el.hash));o.callback.call(this)};var selectTab=function(el){tabs.filter(".selected").removeClass("selected");$(el).parent("li").addClass("selected")};var showContent=function(tabHash){if(tabHash==="#all"){tabContent.show()}else{tabContent.hide().filter(tabHash).fadeIn("fast")}};var autoPlayTimer=function(){timer=setTimeout(function(){autoPlay()},o.autoPlaySpeed)};var autoPlay=function(){if(currentTab<tabsCount-1){currentTab++}else{currentTab=0}tabify(tabLinks.get(currentTab));autoPlayTimer()};if(o.autoPlay){if(tabsCount>1){autoPlayTimer()}}tabLinks.on(o.mouseEvent,function(e){e.preventDefault();tabify(this);if(o.autoPlay){clearTimeout(timer);autoPlayTimer();currentTab=tabLinks.index(this)}});if(o.icon){tabLinks.append("<span></span>")}if(tabs.hasClass("selected")){selectedTab=tabs.filter(".selected").children("a");currentTab=tabLinks.index(selectedTab);showContent(selectedTab.attr("href"))}else{tabs.first().addClass("selected")}})}})(jQuery);